import React from "react";

const Controls = ({ total, size, sort, onChangePageSize, onChangeSort }) => {
  const showingStart = 1;
  const showingEnd = Math.min(size, total);

  return (
    <section className="controls">
      <div>
        Showing {showingStart} - {showingEnd} of {total}
      </div>
      <div>
        Show per page:
        <select value={size} onChange={(e) => onChangePageSize(+e.target.value)}>
          {[10, 20, 50].map((num) => (
            <option key={num} value={num}>{num}</option>
          ))}
        </select>
        Sort by:
        <select value={sort} onChange={(e) => onChangeSort(e.target.value)}>
          <option value="-published_at">Newest</option>
          <option value="published_at">Oldest</option>
        </select>
      </div>
    </section>
  );
};

export default Controls;
