import React, { useEffect, useState } from 'react';
const Header = () => {
  const [show, setShow] = useState(true);
  let last = window.scrollY;
  useEffect(() => {
    const onScroll = () => {
      const cur = window.scrollY;
      setShow(cur < last);
      last = cur;
    };
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);
  return (
    <header className={`main-header`} style={{ top: show ? '0' : '-80px', transition: 'top 0.3s' }}>
      <div className="nav">
        <span className="logo">Suitmedia</span>
        <nav>
          {['Work','About','Services','Ideas','Careers','Contact'].map((item) => (
            <a key={item} href="#" className={item==='Ideas' ? 'active' : ''}>{item}</a>
          ))}
        </nav>
      </div>
    </header>
  );
};
export default Header;
