import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'; // Kalau pakai React Router

const Header = () => {
  const [show, setShow] = useState(true);
  const [isScrolled, setIsScrolled] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(0);

  const location = useLocation(); // Dapatkan path saat ini

  const handleScroll = () => {
    const currentScrollY = window.scrollY;

    setIsScrolled(currentScrollY > 0); // aktifkan efek transparan
    setShow(currentScrollY < lastScrollY); // scroll ke atas → tampil

    setLastScrollY(currentScrollY);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  // Ambil path untuk active menu
  const currentPath = location.pathname;

  const navItems = [
    { name: 'Work', path: '/work' },
    { name: 'About', path: '/about' },
    { name: 'Services', path: '/services' },
    { name: 'Ideas', path: '/ideas' },
    { name: 'Careers', path: '/careers' },
    { name: 'Contact', path: '/contact' },
  ];

  return (
    <header
      className={`main-header ${isScrolled ? 'scrolled' : ''}`}
      style={{ top: show ? '0' : '-80px', transition: 'top 0.3s ease-in-out' }}
    >
      <div className="nav">
        <a href="/" className="logo">
    <img src="/logo.svg" alt="Suitmedia Logo" />
        </a>
        <nav>
          {navItems.map(({ name, path }) => (
            <a
              key={name}
              href={path}
              className={currentPath === path ? 'active' : ''}
            >
              {name}
            </a>
          ))}
        </nav>
      </div>
    </header>
  );
};

export default Header;
